// src/pages/Admin.js

import React, { useState, useEffect } from 'react';
import { supabase } from '../services/supabase';

const Admin = () => {
  const [dashboards, setDashboards] = useState([]);
  const [nome, setNome] = useState('');
  const [descricao, setDescricao] = useState('');
  const [linkPowerBi, setLinkPowerBi] = useState('');
  const [loading, setLoading] = useState(false);
  const [fetchLoading, setFetchLoading] = useState(true);
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');
  const [user, setUser] = useState(null);

  useEffect(() => {
    const fetchUser = async () => {
      const { data: { user } } = await supabase.auth.getUser();
      setUser(user);
    };

    const fetchDashboards = async () => {
      try {
        const { data, error } = await supabase
          .from('dashboards')
          .select('*')
          .order('criado_em', { ascending: false });

        if (error) {
          showMessage('Erro ao carregar dashboards: ' + error.message, 'error');
        } else {
          setDashboards(data || []);
        }
      } catch (err) {
        showMessage('Erro inesperado ao carregar dashboards', 'error');
      } finally {
        setFetchLoading(false);
      }
    };

    fetchUser();
    fetchDashboards();
  }, []);

  const showMessage = (text, type) => {
    setMessage(text);
    setMessageType(type);
    setTimeout(() => {
      setMessage('');
      setMessageType('');
    }, 5000);
  };

  const handleCreateDashboard = async (e) => {
    e.preventDefault();
    
    if (!nome.trim() || !descricao.trim()) {
      showMessage('Nome e descrição são obrigatórios', 'error');
      return;
    }

    setLoading(true);

    try {
      const { data, error } = await supabase
        .from('dashboards')
        .insert([{ 
          nome: nome.trim(), 
          descricao: descricao.trim(), 
          link_power_bi: linkPowerBi.trim() || null 
        }])
        .select()
        .single();

      if (error) {
        showMessage('Erro ao criar dashboard: ' + error.message, 'error');
      } else {
        setDashboards([data, ...dashboards]);
        setNome('');
        setDescricao('');
        setLinkPowerBi('');
        showMessage('Dashboard criado com sucesso!', 'success');
      }
    } catch (err) {
      showMessage('Erro inesperado ao criar dashboard', 'error');
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteDashboard = async (id) => {
    if (!window.confirm('Tem certeza que deseja excluir este dashboard?')) {
      return;
    }

    try {
      const { error } = await supabase
        .from('dashboards')
        .delete()
        .eq('id', id);

      if (error) {
        showMessage('Erro ao excluir dashboard: ' + error.message, 'error');
      } else {
        setDashboards(dashboards.filter(d => d.id !== id));
        showMessage('Dashboard excluído com sucesso!', 'success');
      }
    } catch (err) {
      showMessage('Erro inesperado ao excluir dashboard', 'error');
    }
  };

  const handleLogout = async () => {
    await supabase.auth.signOut();
    window.location.reload();
  };

  return (
    <div className="fade-in">
      <div className="app-header">
        <div className="app-logo">⚙️ Painel Administrativo</div>
        <div className="user-info">
          <span style={{ color: 'var(--gray-600)', fontSize: '0.875rem' }}>
            Admin: {user?.email}
          </span>
          <div className="user-avatar">
            {user?.email?.charAt(0).toUpperCase()}
          </div>
          <button 
            onClick={handleLogout}
            className="btn btn-secondary"
          >
            Sair
          </button>
        </div>
      </div>

      <div className="admin-container">
        {message && (
          <div className={`alert alert-${messageType}`}>
            {message}
          </div>
        )}

        <div className="admin-form">
          <h2 className="section-title">Cadastrar Novo Dashboard</h2>
          
          <form onSubmit={handleCreateDashboard}>
            <div className="form-group">
              <label className="form-label" htmlFor="nome">
                Nome do Dashboard *
              </label>
              <input
                id="nome"
                type="text"
                className="form-input"
                value={nome}
                onChange={(e) => setNome(e.target.value)}
                placeholder="Ex: Vendas Q1 2024"
                required
                disabled={loading}
              />
            </div>
            
            <div className="form-group">
              <label className="form-label" htmlFor="descricao">
                Descrição *
              </label>
              <textarea
                id="descricao"
                className="form-input"
                value={descricao}
                onChange={(e) => setDescricao(e.target.value)}
                placeholder="Descreva o que este dashboard apresenta..."
                rows="3"
                required
                disabled={loading}
                style={{ resize: 'vertical', minHeight: '80px' }}
              />
            </div>
            
            <div className="form-group">
              <label className="form-label" htmlFor="linkPowerBi">
                Link do Power BI (opcional)
              </label>
              <input
                id="linkPowerBi"
                type="url"
                className="form-input"
                value={linkPowerBi}
                onChange={(e) => setLinkPowerBi(e.target.value)}
                placeholder="https://app.powerbi.com/..."
                disabled={loading}
              />
            </div>
            
            <button
              type="submit"
              className="btn btn-success btn-full"
              disabled={loading}
            >
              {loading ? (
                <>
                  <div className="spinner"></div>
                  Criando...
                </>
              ) : (
                '+ Criar Dashboard'
              )}
            </button>
          </form>
        </div>

        <div className="admin-list">
          <h3 className="section-title">Dashboards Cadastrados</h3>
          
          {fetchLoading ? (
            <div className="loading">
              <div className="spinner"></div>
            </div>
          ) : dashboards.length === 0 ? (
            <div className="empty-state">
              <div className="empty-state-icon">📊</div>
              <h3 className="empty-state-title">Nenhum dashboard cadastrado</h3>
              <p className="empty-state-description">
                Crie seu primeiro dashboard usando o formulário acima.
              </p>
            </div>
          ) : (
            <ul className="dashboard-list">
              {dashboards.map((dashboard) => (
                <li key={dashboard.id} className="dashboard-list-item">
                  <div className="dashboard-list-item-content">
                    <h4 className="dashboard-list-item-title">{dashboard.nome}</h4>
                    <p className="dashboard-list-item-description">{dashboard.descricao}</p>
                    {dashboard.link_power_bi && (
                      <p style={{ fontSize: '0.75rem', color: 'var(--success-600)', marginTop: '0.25rem' }}>
                        ✓ Link configurado
                      </p>
                    )}
                  </div>
                  <button
                    onClick={() => handleDeleteDashboard(dashboard.id)}
                    className="btn btn-secondary"
                    style={{ 
                      background: 'var(--error-50)', 
                      color: 'var(--error-600)',
                      border: '1px solid var(--error-200)'
                    }}
                  >
                    Excluir
                  </button>
                </li>
              ))}
            </ul>
          )}
        </div>

        {/* Novo componente de permissões */}
        <DashboardUserPermissionsManager />
      </div>
    </div>
  );
};

// Novo componente para gerenciar permissões de usuários por dashboard
const DashboardUserPermissionsManager = () => {
  const [dashboards, setDashboards] = useState([]);
  const [users, setUsers] = useState([]);
  const [selectedDashboard, setSelectedDashboard] = useState(null);
  const [dashboardUsers, setDashboardUsers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [userToAdd, setUserToAdd] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      const { data: dashboardsData } = await supabase.from('dashboards').select('id, nome');
      setDashboards(dashboardsData || []);
      const { data: usersData } = await supabase.from('usuarios').select('id, email');
      setUsers(usersData || []);
      setLoading(false);
    };
    fetchData();
  }, []);

  useEffect(() => {
    if (selectedDashboard) {
      fetchDashboardUsers(selectedDashboard.id);
    } else {
      setDashboardUsers([]);
    }
  }, [selectedDashboard]);

  const fetchDashboardUsers = async (dashboardId) => {
    setLoading(true);
    const { data: permissions } = await supabase
      .from('user_dashboard_permissions')
      .select('user_id');
    const userIds = (permissions || [])
      .filter(p => p.dashboard_id === dashboardId)
      .map(p => p.user_id);
    setDashboardUsers(userIds);
    setLoading(false);
  };

  const handleAddUser = async () => {
    if (!userToAdd) return;
    setLoading(true);
    await supabase
      .from('user_dashboard_permissions')
      .insert([{ user_id: userToAdd, dashboard_id: selectedDashboard.id }]);
    setDashboardUsers([...dashboardUsers, userToAdd]);
    setUserToAdd('');
    setMessage('Usuário adicionado!');
    setLoading(false);
    setTimeout(() => setMessage(''), 2000);
  };

  const handleRemoveUser = async (userId) => {
    setLoading(true);
    await supabase
      .from('user_dashboard_permissions')
      .delete()
      .eq('user_id', userId)
      .eq('dashboard_id', selectedDashboard.id);
    setDashboardUsers(dashboardUsers.filter(id => id !== userId));
    setMessage('Usuário removido!');
    setLoading(false);
    setTimeout(() => setMessage(''), 2000);
  };

  return (
    <div className="admin-permissions">
      <h3 className="section-title">Permissões de Usuários por Dashboard</h3>
      {message && <div className="alert alert-success">{message}</div>}
      <div className="form-group">
        <label>Selecione um dashboard:</label>
        <select
          className="form-input"
          value={selectedDashboard ? selectedDashboard.id : ''}
          onChange={e => {
            const dash = dashboards.find(d => d.id === parseInt(e.target.value));
            setSelectedDashboard(dash);
          }}
        >
          <option value="">-- Escolha um dashboard --</option>
          {dashboards.map(dash => (
            <option key={dash.id} value={dash.id}>{dash.nome}</option>
          ))}
        </select>
      </div>
      {selectedDashboard && (
        <div className="dashboard-permissions-list">
          <h4>Usuários com permissão:</h4>
          <ul>
            {dashboardUsers.map(userId => {
              const user = users.find(u => u.id === userId);
              return (
                <li key={userId}>
                  {user ? user.email : userId}
                  <button
                    onClick={() => handleRemoveUser(userId)}
                    className="btn btn-secondary"
                    style={{ marginLeft: 8 }}
                    disabled={loading}
                  >
                    Remover
                  </button>
                </li>
              );
            })}
          </ul>
          <div className="form-group" style={{ marginTop: 16 }}>
            <label>Adicionar usuário:</label>
            <select
              className="form-input"
              value={userToAdd}
              onChange={e => setUserToAdd(e.target.value)}
              disabled={loading}
            >
              <option value="">-- Escolha um usuário --</option>
              {users
                .filter(u => !dashboardUsers.includes(u.id))
                .map(u => (
                  <option key={u.id} value={u.id}>{u.email}</option>
                ))}
            </select>
            <button
              onClick={handleAddUser}
              className="btn btn-primary"
              style={{ marginLeft: 8 }}
              disabled={loading || !userToAdd}
            >
              Adicionar
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Admin;
