// src/pages/Admin.js

import React, { useState, useEffect } from 'react';
import { supabase } from '../services/supabase';

const Admin = () => {
  const [dashboards, setDashboards] = useState([]);
  const [nome, setNome] = useState('');
  const [descricao, setDescricao] = useState('');
  const [linkPowerBi, setLinkPowerBi] = useState('');
  const [loading, setLoading] = useState(false);
  const [fetchLoading, setFetchLoading] = useState(true);
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');
  const [user, setUser] = useState(null);

  useEffect(() => {
    const fetchUser = async () => {
      const { data: { user } } = await supabase.auth.getUser();
      setUser(user);
    };

    const fetchDashboards = async () => {
      try {
        const { data, error } = await supabase
          .from('dashboards')
          .select('*')
          .order('created_at', { ascending: false });

        if (error) {
          showMessage('Erro ao carregar dashboards: ' + error.message, 'error');
        } else {
          setDashboards(data || []);
        }
      } catch (err) {
        showMessage('Erro inesperado ao carregar dashboards', 'error');
      } finally {
        setFetchLoading(false);
      }
    };

    fetchUser();
    fetchDashboards();
  }, []);

  const showMessage = (text, type) => {
    setMessage(text);
    setMessageType(type);
    setTimeout(() => {
      setMessage('');
      setMessageType('');
    }, 5000);
  };

  const handleCreateDashboard = async (e) => {
    e.preventDefault();
    
    if (!nome.trim() || !descricao.trim()) {
      showMessage('Nome e descri√ß√£o s√£o obrigat√≥rios', 'error');
      return;
    }

    setLoading(true);

    try {
      const { data, error } = await supabase
        .from('dashboards')
        .insert([{ 
          nome: nome.trim(), 
          descricao: descricao.trim(), 
          link_power_bi: linkPowerBi.trim() || null 
        }])
        .select()
        .single();

      if (error) {
        showMessage('Erro ao criar dashboard: ' + error.message, 'error');
      } else {
        setDashboards([data, ...dashboards]);
        setNome('');
        setDescricao('');
        setLinkPowerBi('');
        showMessage('Dashboard criado com sucesso!', 'success');
      }
    } catch (err) {
      showMessage('Erro inesperado ao criar dashboard', 'error');
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteDashboard = async (id) => {
    if (!window.confirm('Tem certeza que deseja excluir este dashboard?')) {
      return;
    }

    try {
      const { error } = await supabase
        .from('dashboards')
        .delete()
        .eq('id', id);

      if (error) {
        showMessage('Erro ao excluir dashboard: ' + error.message, 'error');
      } else {
        setDashboards(dashboards.filter(d => d.id !== id));
        showMessage('Dashboard exclu√≠do com sucesso!', 'success');
      }
    } catch (err) {
      showMessage('Erro inesperado ao excluir dashboard', 'error');
    }
  };

  const handleLogout = async () => {
    await supabase.auth.signOut();
    window.location.reload();
  };

  return (
    <div className="fade-in">
      <div className="app-header">
        <div className="app-logo">‚öôÔ∏è Painel Administrativo</div>
        <div className="user-info">
          <span style={{ color: 'var(--gray-600)', fontSize: '0.875rem' }}>
            Admin: {user?.email}
          </span>
          <div className="user-avatar">
            {user?.email?.charAt(0).toUpperCase()}
          </div>
          <button 
            onClick={handleLogout}
            className="btn btn-secondary"
          >
            Sair
          </button>
        </div>
      </div>

      <div className="admin-container">
        {message && (
          <div className={`alert alert-${messageType}`}>
            {message}
          </div>
        )}

        <div className="admin-form">
          <h2 className="section-title">Cadastrar Novo Dashboard</h2>
          
          <form onSubmit={handleCreateDashboard}>
            <div className="form-group">
              <label className="form-label" htmlFor="nome">
                Nome do Dashboard *
              </label>
              <input
                id="nome"
                type="text"
                className="form-input"
                value={nome}
                onChange={(e) => setNome(e.target.value)}
                placeholder="Ex: Vendas Q1 2024"
                required
                disabled={loading}
              />
            </div>
            
            <div className="form-group">
              <label className="form-label" htmlFor="descricao">
                Descri√ß√£o *
              </label>
              <textarea
                id="descricao"
                className="form-input"
                value={descricao}
                onChange={(e) => setDescricao(e.target.value)}
                placeholder="Descreva o que este dashboard apresenta..."
                rows="3"
                required
                disabled={loading}
                style={{ resize: 'vertical', minHeight: '80px' }}
              />
            </div>
            
            <div className="form-group">
              <label className="form-label" htmlFor="linkPowerBi">
                Link do Power BI (opcional)
              </label>
              <input
                id="linkPowerBi"
                type="url"
                className="form-input"
                value={linkPowerBi}
                onChange={(e) => setLinkPowerBi(e.target.value)}
                placeholder="https://app.powerbi.com/..."
                disabled={loading}
              />
            </div>
            
            <button
              type="submit"
              className="btn btn-success btn-full"
              disabled={loading}
            >
              {loading ? (
                <>
                  <div className="spinner"></div>
                  Criando...
                </>
              ) : (
                '+ Criar Dashboard'
              )}
            </button>
          </form>
        </div>

        <div className="admin-list">
          <h3 className="section-title">Dashboards Cadastrados</h3>
          
          {fetchLoading ? (
            <div className="loading">
              <div className="spinner"></div>
            </div>
          ) : dashboards.length === 0 ? (
            <div className="empty-state">
              <div className="empty-state-icon">üìä</div>
              <h3 className="empty-state-title">Nenhum dashboard cadastrado</h3>
              <p className="empty-state-description">
                Crie seu primeiro dashboard usando o formul√°rio acima.
              </p>
            </div>
          ) : (
            <ul className="dashboard-list">
              {dashboards.map((dashboard) => (
                <li key={dashboard.id} className="dashboard-list-item">
                  <div className="dashboard-list-item-content">
                    <h4 className="dashboard-list-item-title">{dashboard.nome}</h4>
                    <p className="dashboard-list-item-description">{dashboard.descricao}</p>
                    {dashboard.link_power_bi && (
                      <p style={{ fontSize: '0.75rem', color: 'var(--success-600)', marginTop: '0.25rem' }}>
                        ‚úì Link configurado
                      </p>
                    )}
                  </div>
                  <button
                    onClick={() => handleDeleteDashboard(dashboard.id)}
                    className="btn btn-secondary"
                    style={{ 
                      background: 'var(--error-50)', 
                      color: 'var(--error-600)',
                      border: '1px solid var(--error-200)'
                    }}
                  >
                    Excluir
                  </button>
                </li>
              ))}
            </ul>
          )}
        </div>

        {/* Novo componente de permiss√µes */}
        <DashboardPermissionsManager />
      </div>
    </div>
  );
};

// Componente para gerenciar permiss√µes de dashboards para usu√°rios
const DashboardPermissionsManager = () => {
  const [users, setUsers] = useState([]);
  const [userRoles, setUserRoles] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [userDashboards, setUserDashboards] = useState([]);
  const [allDashboards, setAllDashboards] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      // Buscar todos os usu√°rios
      const { data: usersData } = await supabase.from('users').select('id, email');
      setUsers(usersData || []);
      // Buscar todos os roles
      const { data: rolesData } = await supabase.from('user_roles').select('*');
      setUserRoles(rolesData || []);
      // Buscar todos os dashboards
      const { data: dashboardsData } = await supabase.from('dashboards').select('id, nome');
      setAllDashboards(dashboardsData || []);
      setLoading(false);
    };
    fetchData();
  }, []);

  useEffect(() => {
    if (selectedUser) {
      fetchUserDashboards(selectedUser.id);
    } else {
      setUserDashboards([]);
    }
  }, [selectedUser]);

  const fetchUserDashboards = async (userId) => {
    setLoading(true);
    const { data: permissions } = await supabase
      .from('user_dashboard_permissions')
      .select('dashboard_id');
    const userDashIds = (permissions || [])
      .filter(p => p.user_id === userId)
      .map(p => p.dashboard_id);
    setUserDashboards(userDashIds);
    setLoading(false);
  };

  const handleToggleDashboard = async (dashboardId) => {
    setLoading(true);
    const hasPermission = userDashboards.includes(dashboardId);
    if (hasPermission) {
      // Remover permiss√£o
      await supabase
        .from('user_dashboard_permissions')
        .delete()
        .eq('user_id', selectedUser.id)
        .eq('dashboard_id', dashboardId);
      setUserDashboards(userDashboards.filter(id => id !== dashboardId));
      setMessage('Permiss√£o removida!');
    } else {
      // Adicionar permiss√£o
      await supabase
        .from('user_dashboard_permissions')
        .insert([{ user_id: selectedUser.id, dashboard_id: dashboardId }]);
      setUserDashboards([...userDashboards, dashboardId]);
      setMessage('Permiss√£o adicionada!');
    }
    setLoading(false);
    setTimeout(() => setMessage(''), 2000);
  };

  return (
    <div className="admin-permissions">
      <h3 className="section-title">Permiss√µes de Dashboards por Usu√°rio</h3>
      {message && <div className="alert alert-success">{message}</div>}
      <div className="form-group">
        <label>Selecione um usu√°rio:</label>
        <select
          className="form-input"
          value={selectedUser ? selectedUser.id : ''}
          onChange={e => {
            const user = users.find(u => u.id === e.target.value);
            setSelectedUser(user);
          }}
        >
          <option value="">-- Escolha um usu√°rio --</option>
          {users.map(user => (
            <option key={user.id} value={user.id}>{user.email}</option>
          ))}
        </select>
      </div>
      {selectedUser && (
        <div className="dashboard-permissions-list">
          <h4>Dashboards dispon√≠veis:</h4>
          <ul>
            {allDashboards.map(dash => (
              <li key={dash.id}>
                <label>
                  <input
                    type="checkbox"
                    checked={userDashboards.includes(dash.id)}
                    onChange={() => handleToggleDashboard(dash.id)}
                    disabled={loading}
                  />
                  {dash.nome}
                </label>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Admin;
